import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CatygoryModel, productModel } from 'src/app/models/product-model';
import { ProductsService } from 'src/app/service/products.service';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent implements OnInit{

  public categorys: CatygoryModel[]
  public newProduct: productModel

  public constructor(private productService: ProductsService, private router:Router){}
  async ngOnInit(): Promise<void> {
    try {
      this.categorys = await this.productService.getAllCategories()
      this.router.navigateByUrl("/products")

    } 
    catch (err) {
      console.log (err)

    }    
  }

  public async send(): Promise<void>{
    try {
      await this.productService.addNewProduct(this.newProduct)
      alert ("The product was successfully added")

    } catch (err) {
      console.log (err)
    }
  }

}

 <div>


    <form (ngSubmit)="send()">



        <label>NAME</label>
        <input type="text"[(ngModel)]="newProduct.name" name="name" placeholder="name">

        <label>PRODUCTION</label>
        <input type="datetime-local" [(ngModel)]="newProduct.production" name="production" placeholder="production" >

        <label>EXPIRE</label>
        <input type="datetime-local" [(ngModel)]="newProduct.expire" name="expire" placeholder="expire">

        <label>PRICE</label>
        <input type="number"[(ngModel)]="newProduct.price" name="price" placeholder="price">

        <button>ADD</button>

    </form>
</div>
