import { Component, OnInit } from '@angular/core';
import { CatygoryModel, productModel } from 'src/app/models/product-model';
import { ProductsService } from 'src/app/service/products.service';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent implements OnInit{
  public products: productModel[]
  public categories: CatygoryModel[]
  public constructor(private productService: ProductsService) {}

  async ngOnInit(): Promise <void>{
    try {
      this.products = await this.productService.getAllProduct() 
      this.categories = await this.productService.getAllCategories()     
    } 
    catch (err) {
      console.log(err)      
    }    
  }

public async getProductByCategory(categoryId:string): Promise<void>{
  try {
    if(!categoryId) this.products = await this.productService.getAllProduct()
    this.products = await this.productService.getProductsByCaterory(categoryId)
  } 
  catch (error) {

  }
}



}
